tinymce.PluginManager.add('filemanager', function(editor) {
    function openmanager() {
        var win, data, dom = editor.dom,
            imgElm = editor.selection.getNode();
        var width, height, imageListCtrl;
        win = editor.windowManager.open({

            title: 'Insert image',

            file: tinyMCE.baseURL + '/plugins/filemanager/dialog.php?popup=1&editor=' + editor.id + '&lang=' + tinymce.settings.language + '&subfolder=' + tinymce.settings.subfolder,
            filetype: 'all',
            classes: 'filemanager',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    editor.addButton('filemanager', {
        icon: 'image',
        tooltip: 'Insert image',
        onclick: openmanager,
        stateSelector: 'img:not([data-mce-object])'
    });
    editor.addMenuItem('filemanager', {
        icon: 'image',
        text: 'Insert image',
        onclick: openmanager,
        context: 'insert',
        prependToContext: true
    })
});